import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function NewSubmission() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    college: "",
    dept: "",
    year: "",
    problem: null,
    code: null,
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append("name", formData.name);
    data.append("college", formData.college);
    data.append("dept", formData.dept);
    data.append("year", formData.year);
    data.append("problem", formData.problem);
    data.append("code", formData.code);

    const response = await fetch("http://localhost:5000/evaluate", {
      method: "POST",
      body: data,
    });

    if (response.ok) {
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = "Evaluation_Report.pdf";
      a.click();
    } else {
      alert("Evaluation failed. Please try again.");
    }
  };

  return (
    <div style={{ padding: "30px" }}>
      {/* Back Button */}
      <button
        onClick={() => navigate("/")}
        style={{
          marginBottom: "20px",
          padding: "8px 14px",
          cursor: "pointer",
        }}
      >
        â¬… Back to Dashboard
      </button>

      <h2>New Project Submission</h2>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Student Name"
          onChange={handleChange}
          required
        /><br /><br />

        <input
          type="text"
          name="college"
          placeholder="College Name"
          onChange={handleChange}
          required
        /><br /><br />

        <input
          type="text"
          name="dept"
          placeholder="Department"
          onChange={handleChange}
          required
        /><br /><br />

        <input
          type="text"
          name="year"
          placeholder="Year"
          onChange={handleChange}
          required
        /><br /><br />

        <label>Problem Statement:</label><br />
        <input
          type="file"
          name="problem"
          accept=".pdf,.doc,.docx,.txt"
          onChange={handleFileChange}
          required
        /><br /><br />

        <label>Code ZIP:</label><br />
        <input
          type="file"
          name="code"
          accept=".zip"
          onChange={handleFileChange}
          required
        /><br /><br />

        <button type="submit">Evaluate Project</button>
      </form>
    </div>
  );
}

export default NewSubmission;
