import React, { useEffect, useState } from "react";

function ViewSubmissions() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/submissions")
      .then((res) => res.json())
      .then((result) => {
        setData(result);
        setLoading(false);
      })
      .catch(() => {
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <h3 style={{ textAlign: "center" }}>Loading submissions...</h3>;
  }

  return (
    <div style={{ padding: "30px" }}>
      <h2>Previous Submissions</h2>

      {data.length === 0 ? (
        <p style={{ marginTop: "20px", fontSize: "18px", color: "gray" }}>
          No submissions available yet.
        </p>
      ) : (
        <table border="1" cellPadding="10">
          <thead>
            <tr>
              <th>ID</th>
              <th>Student Name</th>
              <th>Score</th>
              <th>Date</th>
              <th>Report</th>
            </tr>
          </thead>

          <tbody>
            {data.map((item) => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.student_name}</td>
                <td>{item.score}</td>
                <td>{item.date}</td>
                <td>
                  <a
                    href={`http://localhost:5000/download/${item.id}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    Download PDF
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ViewSubmissions;
